import{_ as e,c as t,o as a,d as n}from"./app.174a6592.js";const p=JSON.parse('{"title":"Income Tax return filing due date","description":"","frontmatter":{},"headers":[{"level":2,"title":"Consequence of late","slug":"consequence-of-late"},{"level":2,"title":"AY 2022-23","slug":"ay-2022-23"}],"relativePath":"income-tax/return-filing.md","lastUpdated":1659904343000}'),r={name:"income-tax/return-filing.md"},i=n('<h1 id="income-tax-return-filing-due-date" tabindex="-1">Income Tax return filing due date <a class="header-anchor" href="#income-tax-return-filing-due-date" aria-hidden="true">#</a></h1><p>The due date in case of return filing is the date by which the returns can be filed without any late fee or penalty. You can file the return after due date too. It&#39;s called <a href="./belated-return.html">belated return</a> but interest and penalty will be applicable.</p><p>The due date of filing is dependent on Tax Payer Category.</p><h2 id="consequence-of-late" tabindex="-1">Consequence of late <a class="header-anchor" href="#consequence-of-late" aria-hidden="true">#</a></h2><ol><li>Interest: Interest (u/s 234A) at 1% per month on the unpaid tax amount.</li><li>Late fee: A late fee will need to be paid. 1,000 if the total income is less than 5 lacs. 5000 in case of more than 5 lacs.</li><li>Loss Adjustment: Losses cannot be carried forward to next assessment year.</li></ol><h2 id="ay-2022-23" tabindex="-1">AY 2022-23 <a class="header-anchor" href="#ay-2022-23" aria-hidden="true">#</a></h2><table><thead><tr><th>Category of Taxpayer</th><th>Due Date</th></tr></thead><tbody><tr><td>Individual / HUF/ AOP/ BOI <br>(books of accounts not required to be audited)</td><td>31st July 2022</td></tr><tr><td>Businesses (Requiring Audit)</td><td>31st October 2022</td></tr><tr><td>Businesses (Requiring Transfer Pricing Report)</td><td>30th November 2022</td></tr></tbody></table>',7),d=[i];function o(s,l,c,u,h,f){return a(),t("div",null,d)}var m=e(r,[["render",o]]);export{p as __pageData,m as default};
