import{_ as e,o as t,c as a,Q as r}from"./chunks/framework.1169fbc9.js";const p=JSON.parse('{"title":"Income Tax return filing due date","description":"","frontmatter":{},"headers":[],"relativePath":"income-tax/return-filing.md","filePath":"income-tax/return-filing.md","lastUpdated":1697341928000}'),n={name:"income-tax/return-filing.md"},i=r('<h1 id="income-tax-return-filing-due-date" tabindex="-1">Income Tax return filing due date <a class="header-anchor" href="#income-tax-return-filing-due-date" aria-label="Permalink to &quot;Income Tax return filing due date&quot;">​</a></h1><p>The due date in case of return filing is the date by which the returns can be filed without any late fee or penalty. You can file the return after due date too. It&#39;s called <a href="./belated-return.html">belated return</a> but <a href="#consequence-of-late">interest and penalty</a> will be applicable.</p><p>The due date of filing is dependent on Tax Payer Category.</p><h2 id="consequence-of-late" tabindex="-1">Consequence of late <a class="header-anchor" href="#consequence-of-late" aria-label="Permalink to &quot;Consequence of late&quot;">​</a></h2><ol><li>Interest: Interest (u/s 234A) at 1% per month on the unpaid tax amount.</li><li>Late fee: A late fee will need to be paid. 1,000 if the total income is less than 5 lacs. 5000 in case of more than 5 lacs.</li><li>Loss Adjustment: Losses cannot be carried forward to next assessment year.</li></ol><h2 id="ay-2022-23" tabindex="-1">AY 2022-23 <a class="header-anchor" href="#ay-2022-23" aria-label="Permalink to &quot;AY 2022-23&quot;">​</a></h2><table><thead><tr><th>Category of Taxpayer</th><th>Due Date</th></tr></thead><tbody><tr><td>Individual/ HUF/ AOP/ BOI/ Trust <br>(books of accounts not required to be audited)</td><td>31st July 2022</td></tr><tr><td>Businesses/ Trust <br>(Where Tax Audit is applicable)</td><td>31st October 2022</td></tr><tr><td>Businesses (Where Transfer Pricing Report is applicable) <br> Trust (required to furnish Form 3CEB )</td><td>30th November 2022</td></tr></tbody></table><h2 id="ay-2019-20" tabindex="-1">AY 2019-20 <a class="header-anchor" href="#ay-2019-20" aria-label="Permalink to &quot;AY 2019-20&quot;">​</a></h2><table><thead><tr><th>Category of Taxpayer</th><th>Due Date</th></tr></thead><tbody><tr><td>Individual/ HUF/ AOP/ BOI/ Trust <br>(Non Audit)</td><td>30th November 2020</td></tr><tr><td>Businesses/ Trust <br>(Where Tax Audit is applicable)</td><td>31st December 2020</td></tr><tr><td>Businesses (Where Transfer Pricing Report is applicable) <br> Trust (required to furnish Form 3CEB )</td><td>31st January 2021</td></tr></tbody></table><h3 id="extension" tabindex="-1">Extension <a class="header-anchor" href="#extension" aria-label="Permalink to &quot;Extension&quot;">​</a></h3><ol><li><p>29th October 2022 - The due date was extended via notification no 88/2020 <br> Ref: <a href="https://www.incometaxindia.gov.in/communications/notification/notification_88_2020.pdf" target="_blank" rel="noreferrer">Notification</a></p></li><li><p>30th December 2022 - The due date was further extended <br> Ref: <a href="https://www.incometaxindia.gov.in/news/press-release-extension-of-time-limits.pdf" target="_blank" rel="noreferrer">Press Release</a></p></li></ol>',11),o=[i];function d(s,l,u,c,h,f){return t(),a("div",null,o)}const m=e(n,[["render",d]]);export{p as __pageData,m as default};
